import{_ as t,o as d,c as a,a6 as r}from"./chunks/framework.BvqLMprX.js";const b=JSON.parse('{"title":"Environment Variables","description":"","frontmatter":{"outline":[2,3]},"headers":[],"relativePath":"getting-started/environment-variables.md","filePath":"getting-started/environment-variables.md","lastUpdated":1771429567000}'),i={name:"getting-started/environment-variables.md"};function o(n,e,l,s,c,h){return d(),a("div",null,[...e[0]||(e[0]=[r('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /getting-started/environment-variables.md for this page in Markdown format</div><h1 id="environment-variables" tabindex="-1">Environment Variables <a class="header-anchor" href="#environment-variables" aria-label="Permalink to “Environment Variables”">​</a></h1><h2 id="file-conventions" tabindex="-1">File Conventions <a class="header-anchor" href="#file-conventions" aria-label="Permalink to “File Conventions”">​</a></h2><p>The project uses <a href="https://vite.dev/guide/env-and-mode#env-files" target="_blank" rel="noreferrer">Vite&#39;s env file</a> convention:</p><table tabindex="0"><thead><tr><th>File</th><th>Committed</th><th>Purpose</th></tr></thead><tbody><tr><td><code>.env</code></td><td>Yes</td><td>Shared defaults (placeholder values, no real secrets)</td></tr><tr><td><code>.env.local</code></td><td>No</td><td>Local overrides with real credentials</td></tr><tr><td><code>.env.staging.local</code></td><td>No</td><td>Staging-specific overrides</td></tr><tr><td><code>.env.production.local</code></td><td>No</td><td>Production-specific overrides</td></tr></tbody></table><p><code>.env.local</code> takes precedence over <code>.env</code>. Create it by copying <code>.env</code> and filling in real values:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">cp</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .env</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .env.local</span></span></code></pre></div><div class="warning custom-block"><p class="custom-block-title custom-block-title-default">WARNING</p><p>Never put real secrets in <code>.env</code> – it&#39;s committed to git. Use <code>.env.local</code> for anything sensitive.</p></div><h2 id="cloudflare-worker-bindings" tabindex="-1">Cloudflare Worker Bindings <a class="header-anchor" href="#cloudflare-worker-bindings" aria-label="Permalink to “Cloudflare Worker Bindings”">​</a></h2><p>In production, environment variables are set as Worker secrets or bindings – not from <code>.env</code> files. Configure them in the Cloudflare dashboard or via Wrangler:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">wrangler</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> secret</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> put</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> BETTER_AUTH_SECRET</span></span></code></pre></div><p>Database connections use <a href="https://developers.cloudflare.com/hyperdrive/" target="_blank" rel="noreferrer">Hyperdrive</a> bindings (<code>HYPERDRIVE_CACHED</code>, <code>HYPERDRIVE_DIRECT</code>) instead of raw connection strings. See <a href="/deployment/">Deployment</a> for production setup.</p><p>For local development, Wrangler reads Hyperdrive connection strings from the <code>CLOUDFLARE_HYPERDRIVE_LOCAL_CONNECTION_STRING_*</code> variables in <code>.env</code> / <code>.env.local</code>.</p><h2 id="variable-reference" tabindex="-1">Variable Reference <a class="header-anchor" href="#variable-reference" aria-label="Permalink to “Variable Reference”">​</a></h2><h3 id="application" tabindex="-1">Application <a class="header-anchor" href="#application" aria-label="Permalink to “Application”">​</a></h3><table tabindex="0"><thead><tr><th>Variable</th><th>Required</th><th>Description</th></tr></thead><tbody><tr><td><code>APP_NAME</code></td><td>Yes</td><td>Display name used in emails and passkey prompts</td></tr><tr><td><code>APP_ORIGIN</code></td><td>Yes</td><td>Full origin URL (e.g., <code>http://localhost:5173</code>)</td></tr><tr><td><code>API_ORIGIN</code></td><td>Yes</td><td>API server URL (e.g., <code>http://localhost:8787</code>)</td></tr><tr><td><code>ENVIRONMENT</code></td><td>Yes</td><td><code>development</code>, <code>staging</code>, or <code>production</code></td></tr></tbody></table><h3 id="database" tabindex="-1">Database <a class="header-anchor" href="#database" aria-label="Permalink to “Database”">​</a></h3><table tabindex="0"><thead><tr><th>Variable</th><th>Required</th><th>Description</th></tr></thead><tbody><tr><td><code>DATABASE_URL</code></td><td>Yes</td><td>PostgreSQL connection string</td></tr><tr><td><code>CLOUDFLARE_HYPERDRIVE_LOCAL_CONNECTION_STRING_HYPERDRIVE_CACHED</code></td><td>Dev only</td><td>Hyperdrive cached connection for local dev</td></tr><tr><td><code>CLOUDFLARE_HYPERDRIVE_LOCAL_CONNECTION_STRING_HYPERDRIVE_DIRECT</code></td><td>Dev only</td><td>Hyperdrive direct connection for local dev</td></tr></tbody></table><h3 id="authentication" tabindex="-1">Authentication <a class="header-anchor" href="#authentication" aria-label="Permalink to “Authentication”">​</a></h3><table tabindex="0"><thead><tr><th>Variable</th><th>Required</th><th>Description</th></tr></thead><tbody><tr><td><code>BETTER_AUTH_SECRET</code></td><td>Yes</td><td>Secret for signing sessions and tokens. Generate with <code>bunx @better-auth/cli secret</code></td></tr><tr><td><code>GOOGLE_CLIENT_ID</code></td><td>Yes</td><td>Google OAuth client ID (<a href="https://console.cloud.google.com/apis/credentials" target="_blank" rel="noreferrer">console</a>)</td></tr><tr><td><code>GOOGLE_CLIENT_SECRET</code></td><td>Yes</td><td>Google OAuth client secret</td></tr></tbody></table><p>See <a href="/auth/">Authentication</a> for provider setup details.</p><h3 id="email" tabindex="-1">Email <a class="header-anchor" href="#email" aria-label="Permalink to “Email”">​</a></h3><table tabindex="0"><thead><tr><th>Variable</th><th>Required</th><th>Description</th></tr></thead><tbody><tr><td><code>RESEND_API_KEY</code></td><td>Yes</td><td><a href="https://resend.com" target="_blank" rel="noreferrer">Resend</a> API key for sending emails</td></tr><tr><td><code>RESEND_EMAIL_FROM</code></td><td>Yes</td><td>Sender address (e.g., <code>Your App &lt;noreply@example.com&gt;</code>)</td></tr></tbody></table><h3 id="billing-optional" tabindex="-1">Billing (Optional) <a class="header-anchor" href="#billing-optional" aria-label="Permalink to “Billing (Optional)”">​</a></h3><p>Stripe billing is optional – the app works without these variables, but billing endpoints return 404.</p><table tabindex="0"><thead><tr><th>Variable</th><th>Required</th><th>Description</th></tr></thead><tbody><tr><td><code>STRIPE_SECRET_KEY</code></td><td>No</td><td>Stripe API secret key</td></tr><tr><td><code>STRIPE_WEBHOOK_SECRET</code></td><td>No</td><td>Stripe webhook signing secret</td></tr><tr><td><code>STRIPE_STARTER_PRICE_ID</code></td><td>No</td><td>Stripe Price ID for the Starter plan</td></tr><tr><td><code>STRIPE_PRO_PRICE_ID</code></td><td>No</td><td>Stripe Price ID for the Pro plan (monthly)</td></tr><tr><td><code>STRIPE_PRO_ANNUAL_PRICE_ID</code></td><td>No</td><td>Stripe Price ID for the Pro plan (annual)</td></tr></tbody></table><p>See <a href="/billing/">Billing</a> for Stripe configuration.</p><h3 id="cloudflare" tabindex="-1">Cloudflare <a class="header-anchor" href="#cloudflare" aria-label="Permalink to “Cloudflare”">​</a></h3><table tabindex="0"><thead><tr><th>Variable</th><th>Required</th><th>Description</th></tr></thead><tbody><tr><td><code>CLOUDFLARE_ACCOUNT_ID</code></td><td>Deploy only</td><td>Cloudflare account ID</td></tr><tr><td><code>CLOUDFLARE_ZONE_ID</code></td><td>Deploy only</td><td>DNS zone ID for custom domains</td></tr><tr><td><code>CLOUDFLARE_API_TOKEN</code></td><td>Deploy only</td><td>API token for Wrangler deployments</td></tr></tbody></table><h3 id="analytics-and-search" tabindex="-1">Analytics and Search <a class="header-anchor" href="#analytics-and-search" aria-label="Permalink to “Analytics and Search”">​</a></h3><table tabindex="0"><thead><tr><th>Variable</th><th>Required</th><th>Description</th></tr></thead><tbody><tr><td><code>GA_MEASUREMENT_ID</code></td><td>No</td><td>Google Analytics 4 measurement ID</td></tr><tr><td><code>ALGOLIA_APP_ID</code></td><td>No</td><td>Algolia application ID</td></tr><tr><td><code>ALGOLIA_ADMIN_API_KEY</code></td><td>No</td><td>Algolia admin API key</td></tr></tbody></table>',31)])])}const g=t(i,[["render",o]]);export{b as __pageData,g as default};
