import{_ as e,o as r,c as a,a6 as n}from"./chunks/framework.BvqLMprX.js";const p=JSON.parse('{"title":"Introduction","description":"","frontmatter":{"outline":[2,3]},"headers":[],"relativePath":"getting-started/index.md","filePath":"getting-started/index.md","lastUpdated":1771429567000}'),o={name:"getting-started/index.md"};function i(s,t,d,l,h,c){return r(),a("div",null,[...t[0]||(t[0]=[n('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /getting-started.md for this page in Markdown format</div><h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to “Introduction”">​</a></h1><p>React Starter Kit is a production-ready monorepo for building SaaS web applications. It wires together authentication, database migrations, billing, email, and edge deployment so you can skip months of boilerplate and focus on your product.</p><h2 id="who-it-s-for" tabindex="-1">Who It&#39;s For <a class="header-anchor" href="#who-it-s-for" aria-label="Permalink to “Who It&#39;s For”">​</a></h2><ul><li><strong>Indie hackers</strong> shipping an MVP fast</li><li><strong>Startups</strong> that need a solid foundation without vendor lock-in</li><li><strong>Teams</strong> building multi-tenant SaaS products</li></ul><h2 id="tech-stack" tabindex="-1">Tech Stack <a class="header-anchor" href="#tech-stack" aria-label="Permalink to “Tech Stack”">​</a></h2><table tabindex="0"><thead><tr><th>Layer</th><th>Technology</th></tr></thead><tbody><tr><td>Runtime</td><td><a href="https://bun.sh" target="_blank" rel="noreferrer">Bun</a> 1.3+, TypeScript 5.9, ESM</td></tr><tr><td>Frontend</td><td><a href="https://react.dev" target="_blank" rel="noreferrer">React</a> 19, <a href="https://tanstack.com/router" target="_blank" rel="noreferrer">TanStack Router</a>, <a href="https://tanstack.com/query" target="_blank" rel="noreferrer">TanStack Query</a>, <a href="https://jotai.org" target="_blank" rel="noreferrer">Jotai</a>, <a href="https://tailwindcss.com" target="_blank" rel="noreferrer">Tailwind CSS</a> v4</td></tr><tr><td>UI</td><td><a href="https://ui.shadcn.com" target="_blank" rel="noreferrer">shadcn/ui</a> (new-york style)</td></tr><tr><td>Backend</td><td><a href="https://hono.dev" target="_blank" rel="noreferrer">Hono</a>, <a href="https://trpc.io" target="_blank" rel="noreferrer">tRPC</a> 11</td></tr><tr><td>Auth</td><td><a href="https://www.better-auth.com/" target="_blank" rel="noreferrer">Better Auth</a> – email OTP, passkeys, Google OAuth, organizations</td></tr><tr><td>Billing</td><td><a href="https://stripe.com" target="_blank" rel="noreferrer">Stripe</a> subscriptions via Better Auth plugin</td></tr><tr><td>Database</td><td><a href="https://neon.tech" target="_blank" rel="noreferrer">Neon</a> PostgreSQL, <a href="https://orm.drizzle.team" target="_blank" rel="noreferrer">Drizzle ORM</a></td></tr><tr><td>Email</td><td><a href="https://react.email" target="_blank" rel="noreferrer">React Email</a>, <a href="https://resend.com" target="_blank" rel="noreferrer">Resend</a></td></tr><tr><td>Deployment</td><td><a href="https://developers.cloudflare.com/workers/" target="_blank" rel="noreferrer">Cloudflare Workers</a>, Terraform</td></tr><tr><td>Testing</td><td><a href="https://vitest.dev" target="_blank" rel="noreferrer">Vitest</a> 4, Happy DOM</td></tr></tbody></table><h2 id="what-s-included" tabindex="-1">What&#39;s Included <a class="header-anchor" href="#what-s-included" aria-label="Permalink to “What&#39;s Included”">​</a></h2><ul><li><strong>Three Cloudflare Workers</strong> – edge router, SPA, and API server connected via service bindings</li><li><strong>Type-safe API</strong> – tRPC procedures with Zod validation, shared types between frontend and backend</li><li><strong>Multi-tenant auth</strong> – email OTP, social login, passkeys, organizations with roles</li><li><strong>Subscription billing</strong> – Stripe checkout, webhooks, and plan management</li><li><strong>Database toolkit</strong> – Drizzle ORM schemas, migrations, seeding, and Hyperdrive connection pooling</li><li><strong>Email system</strong> – React Email templates with Resend delivery</li><li><strong>AI-ready</strong> – pre-configured instructions for Claude Code, Cursor, and Gemini CLI</li></ul><h2 id="how-the-docs-are-organized" tabindex="-1">How the Docs Are Organized <a class="header-anchor" href="#how-the-docs-are-organized" aria-label="Permalink to “How the Docs Are Organized”">​</a></h2><p><strong><a href="/getting-started/quick-start">Getting Started</a></strong> covers setup and orientation. <strong><a href="/architecture/">Architecture</a></strong> explains the worker model and request flow. Feature sections – <a href="/frontend/routing">Frontend</a>, <a href="/api/">API</a>, <a href="/auth/">Auth</a>, <a href="/database/">Database</a>, <a href="/billing/">Billing</a> – document each subsystem. <strong><a href="/recipes/new-page">Recipes</a></strong> provide step-by-step guides for common tasks. <strong><a href="/deployment/">Deployment</a></strong> covers shipping to production.</p><p>Ready to start? Head to <a href="./quick-start">Quick Start</a>.</p>',12)])])}const u=e(o,[["render",i]]);export{p as __pageData,u as default};
